#!/usr/bin/env python3

#Python Problem Set 3 - Question 10:

dna = ''

sub_dna = dna[101:201]
print(sub_dna)

#Python Problem Set 3 - Question 11:

print(f'You have {sub_dna.count('G')} Gs in the substring.')

#Python Problem Set 3 - Question 12:

dna12 = ''
sub_dna12 = dna12[101:201]
print(f'You have {sub_dna12.count('G')} Gs in the substring, regardless of case.')

#Python Problem Set 3 - Question 13:

dna_comp  = dna12.upper().replace('A','t').replace('T','a').replace('C','g').replace('G','c')
dna12_rev = dna_comp[::-1]

print(f'The reverse complement of {dna12} is {dna12_rev.upper()}.')



